var randm = function (min, max) {
  return Math.floor(Math.random() * (max - min + 1)) + min;;
}

function capitalize(string) {
  return string.charAt(0).toUpperCase() + string.slice(1);
}

function words(total) {
  var res = [];
  for (var i = 0; i < total; i++) {
    res.push(WORDS[randm(0, WORDS.length - 1)]);
  }

  return res.join(' ');
}

function word() {
  return words(1);
}

function sentences(total) {
  var res = []
  for (var i = 0; i < total; i++) {
    res.push(words(randm(4,15)));
  }

  return capitalize(res.join('. '));
}

function sentence() {
  return sentences(1);
}

function paragraph() {
  return paragraphs(1);
}

function paragraphs(total) {
  var res = []
  for (var i = 0; i < total; i++) {
    res.push(sentences(randm(3,7)));
  }

  return res.join('\n\n');
}

function date() {
  var y = randm(1, 20) + 1990;
  var m = randm(0, 11);
  var d = randm(1, 31);

  return new Date(y, m, d);
}

function firstName() {
  return FIRSTNAMES[randm(0, FIRSTNAMES.length - 1)];
}

function lastName() {
  return LASTNAMES[randm(0, LASTNAMES.length - 1)];
}

function name() {
  return firstName() + ' ' + lastName();
}

function tweet() {
  return TWEETS[randm(0, TWEETS.length - 1)];
}

function email() {
  var username = name().replace(/[^\w]/, DELIMITERS[randm(0, DELIMITERS.length - 1)])
  return username + '@' + DOMAINS[randm(0, DOMAINS.length - 1)].toLowerCase();
}

function country() {
  return COUNTIES[randm(0, COUNTIES.length - 1)];
}

module.exports = {
  words: words,
  word: word,
  sentence: sentence,
  sentences: sentences,
  paragraph: paragraph,
  paragraphs: paragraphs,
  date: date,
  firstName: firstName,
  lastName: lastName,
  name: name,
  tweet: tweet,
  email: email,
  country: country
};
